import React,{useState} from 'react'
function User2() {
  const [val,setVal]=useState("0")

   return (
    <div className="App">
      <h1>Arslan </h1>
      <input type="text" value={val} onChange={(e)=>setVal(e.target.value)} />
   <h1>{val}</h1>
    </div>
  );

}
export default  User2;
