import React,{useState} from 'react'
function App(){
    const  [name, setName]=useState("");
    const[tnc,setTnc]=useState(false);
    const [interest,setInterest]= useState("");
    function getFormData(e)
    {
        console.log(name,tnc,interest);
        e.preventDefault()
    }
    return(
        <div className="App">
            <h1>Handle Form in React</h1>
            <form onSubmit={getFormData}>
        <input  type="text" placeholder="Enter name"> onChange={(e)=>setName(e.target.value)}</input> <br></br> <br></br>
        <select onChange={(e)=>setInterest(e.target.value)}>
    <option>Select Optionsn</option>
    <option>Marvel</option>
    <option>JK</option>
         </select> <br></br> <br></br>
    <input type="checkbox"  onChange={(e)=>setTnc(e.target.checked)}/> <span>Accept Terms And Conditions</span> <br></br> <br></br>
     <button type="submit">  Submit </button>
    </form>
        </div>
    );
}
export default App;



